function [fitresult, gof] = fit_for_line(x, y, w)
%CREATEFIT(X,Y,W)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input: x
%      Y Output: y
%      Weights: w
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 05-Mar-2024 09:35:37


%% Fit: 'untitled fit 1'.
[xData, yData, weights] = prepareCurveData( x, y, w );

% Set up fittype and options.
ft = fittype( 'poly1' );
excludedPoints = excludedata( xData, yData, 'Indices', [3 7] );
opts = fitoptions( 'Method', 'LinearLeastSquares' );
opts.Lower = [196 0];
opts.Weights = weights;
opts.Exclude = excludedPoints;

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
figure( 'Name', 'Delta Between Fitted Function vs. Height' );
title("Delta Between Fitted Function vs. Height")
h = plot( fitresult, xData, yData, excludedPoints );
legend( h, 'Delta Between Fitted Function', 'Outliers', 'Polynomial Trend', 'Location', 'NorthEast', 'Interpreter', 'none' );
% Label axes
xlabel( 'Height [m]', 'Interpreter', 'none' );
ylabel( 'Delta Between Fitted Function', 'Interpreter', 'none' );
grid on


